"use strict";
function join() {
        socket = io(),
        socket.username = document.getElementById("a").value,
        socket.on("connect", function () {
            console.log(socket.id),
            socket.emit("username", document.getElementById("a").value)
        }),
        socket.on("msg", function (e) {
            console.log(e), console.log(socket.id);
            var n = document.createElement("div");
            e.socketid === socket.id ? n.className = "mychat" : n.className = "chat";
            var t = document.createElement("span");
            t.innerText = e.username + " : ", t.className = "bb";
            var o = document.createElement("span");
            o.innerText = e.data, o.className = "b",
                n.appendChild(t),
                n.appendChild(o),
                document.getElementById("main").appendChild(n)
        }),
        socket.on("welcome", function (e) {
            console.log(e)
        }),
        socket.on("news", function (e) {
            console.log(e)
        }),
        socket.on("hello", function (e) {
            console.log(e);
            var n = document.createElement("div");
            n.style.textAlign = "center",
                n.style.color = "#888",
                n.style.fontSize = "12px",
                n.innerText = e,
                document.getElementById("main").appendChild(n)
        })
        socket.on("voice", function (e) {
            // console.log(e);
            document.getElementById('audio').src = e.data
        })
}
